import { from, lastValueFrom } from 'rxjs';
import { OKTA_AUTH } from '@okta/okta-angular';
import { environment } from 'src/environments/environment';
import * as i0 from "@angular/core";
import * as i1 from "@okta/okta-auth-js";
export class AuthInterceptorService {
    constructor(oktaAuth) {
        this.oktaAuth = oktaAuth;
    }
    intercept(request, next) {
        return from(this.handleAccess(request, next));
    }
    async handleAccess(request, next) {
        const theEndpoint = environment.HCLTechStoreUrl + '/orders';
        const securedEndpoints = [the];
        if (securedEndpoints.some(url => request.urlWithParams.includes(url))) {
            const accessToken = await this.oktaAuth.getAccessToken();
            console.log(accessToken);
            request = request.clone({
                setHeaders: {
                    Authorization: "Bearer " + accessToken
                }
            });
        }
        // return next.handle(request).toPromise();
        const vari = lastValueFrom(next.handle(request));
        return vari;
    }
}
